application
        android:allowBackup=
true

        android:icon=
@drawable/ic_launcher

        android:label=
@string/app_name

        android:theme=
@style/AppTheme


activity
            android:name=
cn.bmob.example.MainActivity

            android:screenOrientation=
portrait

            android:label=
@string/app_name



action android:name=
android.intent.action.MAIN
