BmobUser *boss           = [BmobUser objectWithoutDataWithClassName:@
User
 objectId:@
xxxxxx
];
BmobUser *hr_zhang       = [BmobUser objectWithoutDataWithClassName:@
User
 objectId:@
xxxxxx
];
BmobUser *hr_luo         = [BmobUser objectWithoutDataWithClassName:@
User
 objectId:@
xxxxxx
];;
BmobUser *cashier_xie    = [BmobUser objectWithoutDataWithClassName:@
User
 objectId:@
xxxxxx
];
BmobUser *me             = [BmobUser objectWithoutDataWithClassName:@
User
 objectId:@
