    [inQuery whereKey:@ username  equalTo:@ user2 ];

    //匹配查询
    [bquery whereKey:@ author  matchesQuery:inQuery];
    [bquery findObjectsInBackgroundWithBlock:^(NSArray *array, NSError *error) {
        if (error) {
            NSLog(@ %@ ,error);
        } else if (array){
            for (BmobObject *post in array) {
                NSLog(@ %@ ,[post objectForKey:@ title ]);
            }
        }
    }];  如果想要查询找所有没有关联user1的文章，则将  [bquery whereKey:@ author  matchesQuery:inQuery];  替换成  [bquery whereKey:@ author  doesNotMatchQuery:inQuery];  即可。

