    $res = $bmobSchemas- createSchemas( e09fb5cbb5bxxxxxxxx9504604c0dcff ,  City , $data);

在表“City”中添加字段“name”      $data=array(
             className  =   City ,
             fields  =  array(
               name  =  array(
                 type = String ,
              ),
            ),
        );
    $res = $bmobSchemas- updateSchemas( e09fb5cbb5bxxxxxxxx9504604c0dcff ,  City , $data);   在表“City”中删除字段“name”      $data=array(
             className  =   City ,
