  db.find({
     table : Games 
  },function(err,data){ 
  //将返回结果转换为Json对象
  var resultObject= JSON.parse(data);
  //遍历这个Json对象
  for(var results in resultObject)
  {
    var resultArr = resultObject[results];
    var str =   ;
    //遍历得到的每行结果
